const findJSONConfigs = require('./util/findRichmediaRC');
const createConfigByRichmediarcList = require('./webpack/config/createConfigByRichmediarcList');
const webpack = require('webpack');
const inquirer = require('inquirer');

module.exports = async function build() {
  const allConfigsSelector = './**/.richmediarc';

  const configs = await findJSONConfigs(allConfigsSelector, [
    'settings.entry.js',
    'settings.entry.html',
  ]);
  const questions = [];

  questions.push(
    {
      type: 'list',
      name: 'build',
      message: 'Please choose the current build to start.',
      choices: ['ALL', ...configs.map(({ location }) => location)],
    },
    {
      type: 'list',
      name: 'buildTarget',
      message: 'Please choose build location',
      choices: ['./build', 'other location'],
    },
  );

  let answers = await inquirer.prompt(questions);

  if (answers.buildTarget === 'other location') {
    answers = {
      ...answers,
      ...(await inquirer.prompt([
        {
          type: 'input',
          name: 'buildTarget',
          message: 'Please type in build location',
          default: './build',
        },
      ])),
    };
  }

  let configsResult = null;

  if (answers.build === 'ALL') {
    configsResult = configs;
  } else {
    configsResult = configs.filter(({ location }) => location === answers.build);
  }

  const result = await createConfigByRichmediarcList(configsResult);

  const compiler = webpack(result).run((err, stats) => {
    console.log(err);
  })
};

const path = require('path');
const fs = require('fs');
const webpack = require('webpack');
const webpackDevMiddleware = require('webpack-dev-middleware');
const webpackHotMiddleware = require('webpack-hot-middleware');
const express = require('express');
const handlebars = require('handlebars');
const portscanner = require('portscanner');

const templatePromise = Promise.resolve(true).then(
  () =>
    new Promise((resolve, reject) => {
      fs.readFile(
        path.join(__dirname, './data/template.hbs'),
        { encoding: 'utf-8' },
        (err, data) => {
          if (err) {
            reject(err);
          } else {
            resolve(handlebars.compile(data));
          }
        },
      );
    }),
);

function getNameFromSetting(setting) {
  const urls = path.dirname(setting.location).split('/');
  return urls[urls.length - 1];
}

/**
 *
 * @param {Array<{webpack: *, settings: {location, data}}>} configs
 */
module.exports = function devServer(configs) {
  const webpackConfigList = configs.map(({ webpack }) => webpack);
  const settingList = configs.map(({ settings }) => settings);

  templatePromise.then(template => {
    const app = express();

    webpackConfigList.forEach((config, index) => {
      const compiler = webpack(config);

      app.use(
        webpackDevMiddleware(compiler, {
          publicPath: `/${getNameFromSetting(settingList[index])}/`,
        }),
      );
      app.use(webpackHotMiddleware(compiler));
    });


    app.get('/', (req, res) => {
      const templateConfig = {
        banner: settingList.map(value => {
          const name = getNameFromSetting(value);

          return {
            src: `./${name}/`,
            name,
            width: value.data.settings.size.width,
            height: value.data.settings.size.height,
          };
        }),
      };

      res.send(template(templateConfig));
    });

    portscanner.findAPortNotInUse(3000, 3010, '127.0.0.1', function(error, port) {
      app.listen(port, () => console.log(`Example app listening on http://localhost:${port}`));
    });

    // eslint-disable-next-line

    process.on('uncaughtException', e => {
      // eslint-disable-next-line
      console.log(e);
      // app.close();
    });
    process.on('SIGTERM', () => app.close());
  });
};

const findJSONConfigs = require('./util/findRichmediaRC');
const configGeneratorByRichmediarcList = require('./webpack/config/createConfigByRichmediarcList');
const devServer = require('./dev-server');
const inquirer = require('inquirer');

module.exports = async function dev(allConfigsSelector = './**/.richmediarc') {
  const configs = await findJSONConfigs(allConfigsSelector, [
    'settings.entry.js',
    'settings.entry.html',
  ]);

  if (configs.length === 0) {
    throw new Error('could not find a compatible .richmediarc with entry points configured');
  }

  let questions;

  const answers = await inquirer.prompt([
    {
      type: 'list',
      name: 'devLocation',
      message: 'Please choose the current build to start developing.',
      choices: ['ALL', ...configs.map(({ location }) => location)],
    },
  ]);

  let configsResult = null;

  if (answers.devLocation === 'ALL') {
    configsResult = configs;
  } else {
    configsResult = configs.filter(({ location }) => location === answers.devLocation);
  }

  const result = await configGeneratorByRichmediarcList(configsResult, 'development');

  const list = result.map((webpack, index) => ({ webpack, settings: configsResult[index] }));

  devServer(list);
};

