  if (mode === 'production') {
    devtool = false;
  }

  if(mode === 'development'){
    entry.push(`webpack-hot-middleware/client`);
  }

  entry.push(filepathJs);

  const config = {
    mode,
    entry,

    output: {
      path: outputPath,
      filename: 'main.js',
    },
    externals: {
      // gsap external
      TweenLite: 'TweenLite',
      TweenMax: 'TweenMax',
      TimelineLite: 'TimelineLite',
      TimelineMax: 'TimelineMax',

      // doubleclick and monet external
      Enabler: 'Enabler',
      Monet: 'Monet',
    },
    resolve: {
      modules: ['node_modules', nodeModules],
      alias: {
        vendor: path.resolve(__dirname, '../../../vendor'),
      },
    },

    resolveLoader: {
      modules: ['node_modules', nodeModules],
      symlinks: true,
    },

    module: {
      rules: [
        {
          test: /\.css$/,
          use: [
            {
              loader: 'file-loader',
              options: {
                name: '[name].css',
              },
            },
            {
              loader: 'extract-loader',
            },
